import { Injectable } from '@angular/core';
import { Booking } from './booking';
import { Bookings } from './mock-bookings';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class BookingService {

  constructor(private httpClient: HttpClient) { }

  // aus dem createDb() das bookings deswegen heißt es hier so in der Url. /api ist frei wählbar
  bookingsUrl: string = "/api/bookings"

  getBookings(): Observable<Booking[]>{

    var response = this.httpClient.get<Booking[]>(this.bookingsUrl);

    return response;
  }

  deleteBooking(booking: Booking): Observable<Booking>{

    var response = this.httpClient.delete<Booking>(this.bookingsUrl+"/0"+booking.id);

    return response;
  }

  getBookingById(id: number):Booking{
    var bookingById = Bookings.find(b => b.id == id)!;
    return bookingById;
  }

  addBooking(booking: Booking): void{
    Bookings.push(booking);
  }

  updateBooking(booking: Booking): void {
    let currentBooking = this.getBookingById(booking.id);
    currentBooking = booking;
  }
}
